$(document).ready(function() {
    // Clonar a primeira vitrine
    var $vitrineClone = $('.product-showcase').first().clone(); // Clona a primeira vitrine

    // Inserir a vitrine clonada após a seção .banners
    $('.banners').after($vitrineClone);

    // Adicionar uma classe para diferenciar o clone, se necessário
    $vitrineClone.addClass('cloned-vitrine');
});

